import React  from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';

class App extends React.Component {
	constructor(){
		super();
		this.state={
			rob:[],
			schfield:''
		}

	}
	componentDidMount(){
		fetch('https://jsonplaceholder.typicode.com/users')
		.then(response => response.json())
		.then(users => this.setState({rob : users}));

	}

	onSearchChange= (event)=>{
		this.setState({schfield : event.target.value});
	}
	render(){
		const robfilter = this.state.rob.filter(rob => {
			return rob.name.toLowerCase().includes(this.state.schfield.toLowerCase());
		});
		return (
			<div className='tc'>
				<h1> RoboFriends </h1>
				<SearchBox  SearchChange={this.onSearchChange} />
				<CardList robots={robfilter}/>
			</div>
			);
	}
}



export default App;
